import React, { useState, useEffect, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useParams } from 'react-router-dom';
import { useLocation } from "react-router";

import Container from '../components/Container'
import Nav from '../components/Nav';
import '../index.css'

import 'github-markdown-css'

// 文章页面
function Post(props) {
    let { slug } = useParams();


    const useScrollToTop = () => {   //注意自定义Hooks要用useXXX定义
        const { pathname } = useLocation();
        useEffect(() => {
            console.log(pathname);
            console.log('useScrollToTop');

            window.scrollTo(0, 0);

        }, [pathname]);
    }


    const useReStoreScrollTop = () => {
        const { pathname } = useLocation();
        useEffect(() => {
            try {
                let scrollY = sessionStorage.getItem(pathname)
                scrollY && window.scrollTo(0, scrollY)
            } catch (error) {
                throw error
            }
            return () => {
                try {
                    sessionStorage.setItem(pathname, window.scrollY)    //这里使用scrollY是获取文档window在垂直页面
                } catch (error) {                                     //的滚动值，如果是特定DOM元素用srcollTop
                    throw error
                }
            };
        }, [pathname])
    }

    useScrollToTop();
    // useReStoreScrollTop();

    useEffect(() => {
        // console.log('scrollTo(0, 0)');
        // window.scrollTo(0, 0);
        console.log('Post useEffect');

    })

    document.title = props.title

    return <div>

        <div>
            <Nav />
            <Container post_id={slug} />
        </div>
    </div>;

}

export default Post;
