const getHeptabaseData = () => {
    console.log('getHeptabaseData');

    // 获取本地数据
    let heptabase_blog_data = localStorage.getItem("heptabase_blog_data")
    console.log('local_heptabase_blog_data:');
    console.log(heptabase_blog_data);
    // 若本地存在数据则不重新获取
    if (heptabase_blog_data != undefined) {
        heptabase_blog_data = JSON.parse(heptabase_blog_data)
    } else {
        // 本地无数据
        // 获取 Heptabase 数据
        fetch(
            'https://app.heptabase.com/api/whiteboard/?secret=d4cc3728297609add1a00aab108e90c4e57a1c378cfc2307c251745bf7d2a884'
        )
            .then(res => res.json())
            .then(data => {
                console.log(data)
                heptabase_blog_data = data
                // 存储数据到本地缓存
                localStorage.setItem("heptabase_blog_data", JSON.stringify(data))
                // console.log(this.state.posts);
            })
            .catch(e => console.log('错误:', e))
    }

    return heptabase_blog_data
}

export { getHeptabaseData }
