import React, { useState, useEffect, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useParams } from 'react-router-dom';
import { useLocation } from "react-router";

import useHash from "../hooks/useHash";

import Container from '../components/Container'
import Nav from '../components/Nav';
import Footer from '../components/Footer'

import '../index.css'
import 'github-markdown-css'

import { getHeptabaseData, getClearCard, getClearImag } from '../constantFunction'
import { set } from 'date-fns';

// 文章页面
function Post(props) {
    let { slug } = useParams();



    // useScrollToTop();

    // 处理 hash
    const [hash, setHash] = useHash();
    // 记录数据加载状态
    let [isLoading, setLoadingState] = useState(true)
    // 记录数据
    let [cards, setCards] = useState([])

    let [card_list, setCardList] = useState([])

    let container_list





    // hash 变化时
    useEffect(() => {

        console.log('hash change useEffect');
        // 更新数据
        console.log(hash);

        // setCards([])




    }, [hash])

    // 页面载入
    useEffect(() => {
        // console.log('scrollTo(0, 0)');
        // window.scrollTo(0, 0);
        console.log('Post useEffect');
        // 获取 url
        console.log(slug);

    })



    // cards 变化时
    useEffect(() => {

        console.log('cards change useEffect');


    }, [cards])


    // 获取文章数据、处理文章数据
    const setContent = (id) => {
        console.log('setContent');

        let mark = -1
        let isExists = false

        // setCards([])

        for (let j = 0; j < cards.length; j++) {

            // 数据是否已在列表中
            if (cards[j]['card']['id'] == id) {
                isExists = true
            }

        }

        if (isExists) {
            return
        }




        // 存储数据的变量
        let heptabase_blog_data

        getHeptabaseData.then((res) => {
            heptabase_blog_data = res.data
            let new_card = null
            console.log('Container setContent for:');
            for (let i = 0; i < heptabase_blog_data.cards.length; i++) {

                if (heptabase_blog_data.cards[i]['id'] == id) {

                    // 处理内容中的图片
                    heptabase_blog_data.cards[i] = getClearImag(heptabase_blog_data.cards[i])
                    console.log('getClearImag done');
                    // 处理内容中的链接
                    new_card = getClearCard(heptabase_blog_data.cards[i], heptabase_blog_data.cards)
                    heptabase_blog_data.cards[i] = new_card['card']



                    cards.push(new_card)

                    let new_card_compent = <Container key={new_card['id']} index={new_card['id']} data={new_card} handleLinkClick={handleLinkClick} />
                    // card_list = 
                    // setCardList(card_list.push(new_card))
                    // console.log(cards);
                    setLoadingState(false)

                    break;
                }
            }

            setCards(cards)

            // 404
            if (new_card == null) {
                console.log('404');
                // window.location = '/404'
            }
        })
    }

    const useScrollToTop = () => {   //注意自定义Hooks要用useXXX定义
        const { pathname } = useLocation();
        useEffect(() => {

            console.log('pathname change');

            console.log('useScrollToTop');

            console.log(window.history);
            window.history.scrollRestoration = 'manual';

            // 根据跳转类型来决定是否要定位到顶部，如果不是点击 span 或 backLink 则不回到顶部

            let nav_type = sessionStorage.getItem('nav_type')

            if (nav_type > -1) {
                // 点击了 span 或 backLink

                setTimeout(() => {
                    window.scrollTo({
                        top: 0,
                        left: 0,
                        behavior: 'auto'
                    });
                }, 10);

                // window.scrollTo(0, 0);

                // 重置 nav_type
                sessionStorage.setItem('nav_type', -1)
            } else {

                // 返回上一页
                setTimeout(() => {

                    window.scrollTo({
                        top: sessionStorage.getItem('scrollY'),
                        left: 0,
                        behavior: 'auto'
                    });

                    // window.scrollTo(0, sessionStorage.getItem('scrollY'));

                }, 10);

                // window.scrollTo(0, sessionStorage.getItem('scrollY'));


            }


        }, [pathname]);
    }

    // 设置 hash
    const postSetHash = (targe_card_id, current_card_id) => {
        //移除旧的
        let newHash = ''
        let id_list = hash.split('#')

        for (let j = 0; j < id_list.length; j++) {

            // ABC 中点击 A 卡片的链接时，删除 A 之后的所有数据
            if (id_list[j] == current_card_id) {

                if (j !== id_list.length - 1) {
                    // 在最后 1 个卡片中点击则不删除任何数据

                    // 删除 hash
                    id_list.splice(j + 1, id_list.length)
                    // cards.splice(j + 1, cards.length)


                    break;
                }

            }

        }

        newHash = id_list.join('#')
        newHash = newHash + '#' + targe_card_id
        setHash(newHash)


    }

    // 点击链接
    const handleLinkClick = (id, card_index) => {
        console.log('Post handleLinkClick');
        console.log(id);
        console.log(card_index);
        // if (hash == '') {
        //     setHash(hash + id)
        // } else {
        //     setHash(hash + '#' + id)
        // }

        setContent(id)
        postSetHash(id, card_index)
    }


    const setCardContainer = (cards) => {
        let list = cards.map((card) =>
            <Container key={card['card']['id']} index={card['card']['id']} data={card} handleLinkClick={handleLinkClick} />
        )

        return list
    }

    document.title = props.title
    console.log(cards);
    // let container_list = cards.map((card)=>{
    //     <Container card={card} />  
    // })
    console.log(card_list);


    // 获取数据
    // ID 列表
    let id_list = hash.split('#')

    for (let i = 0; i < id_list.length; i++) {
        if (id_list[i] !== '') {
            setContent(id_list[i])
        }
    }
    console.log(cards);

    //删除 hash 中不存在的数据
    for (let j = 0; j < cards.length; j++) {
        let isExists = false
        let cardInCardsIndex = -1
        id_list.forEach(id => {
            if (id === cards[j]['card']['id']) {
                isExists = true
                cardInCardsIndex = j
            }
        });

        if (isExists !== true) {
            // hash 中不存在此卡片，删除此数据
            cards.splice(cardInCardsIndex, 1)
            setCards(cards)
        }
    }



    container_list = setCardContainer(cards)



    console.log(container_list);


    return <div>

        <Nav />
        <div className='container_list'>
            {container_list}
        </div>
        <Footer />

    </div>;

}

export default Post;
