import React from 'react';
import { BrowserRouter ,Router, Routes,Route,Switch } from 'react-router-dom';
import Home from './page/Home';
import Post from './page/Post';
import Empty from './page/404';
import About from './page/About';

class App extends React.Component {
    render() {
        return (

            <Router >
                <Switch>
                    <Routes>
                        <Route path="/" element={<Home />} />
                        <Route exact path="/post/:id" element={<Post props={window.location.pathname} />} />

                        {/* <Route exact path="/post/:id" render={()=>{
                            console.log('renderrenderrenderrenderrender');
                            return <Post />
                        }} /> */}

                        <Route path="/about/" element={<About />} />
                        <Route path="/404/" element={<Empty />} />
                        <Route element={<Empty />} />
                    </Routes>
                </Switch>
            </Router>
        )
    }
}
export default App;
