import React, { useState, useEffect, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useParams } from 'react-router-dom';
import { useLocation } from "react-router";

import useHash from "../hooks/useHash";

import Container from '../components/Container'
import Nav from '../components/Nav';
import Footer from '../components/Footer'

import '../index.css'
import 'github-markdown-css'

import { getHeptabaseData, getClearCard, getClearImag } from '../constantFunction'

// 文章页面
function Post(props) {
    let { slug } = useParams();


    const useScrollToTop = () => {   //注意自定义Hooks要用useXXX定义
        const { pathname } = useLocation();
        useEffect(() => {

            console.log('pathname change');

            console.log('useScrollToTop');

            console.log(window.history);
            window.history.scrollRestoration = 'manual';

            // 根据跳转类型来决定是否要定位到顶部，如果不是点击 span 或 backLink 则不回到顶部

            let nav_type = sessionStorage.getItem('nav_type')

            if (nav_type > -1) {
                // 点击了 span 或 backLink

                setTimeout(() => {
                    window.scrollTo({
                        top: 0,
                        left: 0,
                        behavior: 'auto'
                    });
                }, 10);

                // window.scrollTo(0, 0);

                // 重置 nav_type
                sessionStorage.setItem('nav_type', -1)
            } else {

                // 返回上一页
                setTimeout(() => {

                    window.scrollTo({
                        top: sessionStorage.getItem('scrollY'),
                        left: 0,
                        behavior: 'auto'
                    });

                    // window.scrollTo(0, sessionStorage.getItem('scrollY'));

                }, 10);

                // window.scrollTo(0, sessionStorage.getItem('scrollY'));


            }


        }, [pathname]);
    }

    // useScrollToTop();

    // 处理 hash
    const [hash, setHash] = useHash();
    // 记录数据加载状态
    let [isLoading, setLoadingState] = useState(true)
    // 记录数据
    const [cards, setCards] = useState([])

    // 页面载入
    useEffect(() => {
        // console.log('scrollTo(0, 0)');
        // window.scrollTo(0, 0);
        console.log('Post useEffect');
        // 获取 url
        console.log(slug);
        // 获取数据
        console.log(cards);
        setContent(slug)

    })


    // hash 变化时
    useEffect(() => {

        console.log('hash change useEffect');

    }, [hash])


    // cards 变化时
    useEffect(() => {

        console.log('cards change useEffect');

    }, [cards])

    // 获取文章数据、处理文章数据
    const setContent = (id) => {
        console.log('setContent');

        // 存储数据的变量
        let heptabase_blog_data

        getHeptabaseData.then((res) => {
            heptabase_blog_data = res.data
            let new_card = null
            console.log('Container setContent for:');
            for (let i = 0; i < heptabase_blog_data.cards.length; i++) {

                if (heptabase_blog_data.cards[i]['id'] == id) {

                    // 处理内容中的图片
                    heptabase_blog_data.cards[i] = getClearImag(heptabase_blog_data.cards[i])
                    console.log('getClearImag done');
                    // 处理内容中的链接
                    new_card = getClearCard(heptabase_blog_data.cards[i], heptabase_blog_data.cards)
                    heptabase_blog_data.cards[i] = new_card['card']

                    cards.push(new_card)
                    setCards(cards)
                    console.log(cards);
                    setLoadingState(false)
                    break;
                }
            }

            // 404
            if (new_card == null) {
                console.log('404');
                window.location = '/404'
            }
        })

    }

    let container_list = []
    // 点击链接
    const handleLinkClick = (id) => {   
        console.log('Post handleLinkClick');
        console.log(id);
        setContent(id)
    }




    document.title = props.title
    console.log(cards);
    // let container_list = cards.map((card)=>{
    //     <Container card={card} />  
    // })


    for (let i = 0; i < cards.length; i++) {
        // console.log(cards[i]);
        container_list.push(<Container data={cards[i]} handleLinkClick={handleLinkClick} />)
    }

    console.log(container_list);

    return <div>


        <Nav />
        <div className='container_list'>
            {container_list}
        </div>
        <Footer />

    </div>;

}

export default Post;
