// import React from 'react';
import React, { useState, useEffect } from 'react';

import Nav from '../components/Nav';
import '../index.css'
import { Link } from 'react-router-dom';
import { getHeptabaseData, getClearCard, getClearImag } from '../constantFunction'



class Post extends React.Component {

    constructor(props) {
        super(props);
        this.state = { content: '', backLinks: [], isLoading: true };
    }

    UNSAFE_componentWillMount() {
        //console.log(this.props.location)//传递过来的所有参数
        // console.log(this.props.location.state.key)//val值

        console.log('Post UNSAFE_componentWillMount:');
        console.log(this.props);

        let post_id = window.location.pathname.replace('/post/', '')
        this.setContent(post_id)

        // this.props.history.listen = () => {
        //     console.log(this.props.history);
        // }

    }

    UNSAFE_componentWillReceiveProps() {
        console.log('Post UNSAFE_componentWillReceiveProps');
    }

    setContent = (id) =>{

        let heptabase_blog_data
        getHeptabaseData.then((res) => {
            heptabase_blog_data = res.data
            console.log(res);
            //获取 URL 中的文章 ID

            console.log('Post setContent for:');
            for (let i = 0; i < heptabase_blog_data.cards.length; i++) {

                if (heptabase_blog_data.cards[i]['id'] == id) {

                    // 处理内容中的图片
                    heptabase_blog_data.cards[i] = getClearImag(heptabase_blog_data.cards[i])
                    console.log('getClearImag done');
                    // 处理内容中的链接
                    let new_card = getClearCard(heptabase_blog_data.cards[i], heptabase_blog_data.cards)
                    heptabase_blog_data.cards[i] = new_card['card']

                    console.log('backLinks:');
                    console.log(new_card['backLinks']);

                    this.setState({ content: heptabase_blog_data.cards[i]['content'], backLinks: new_card['backLinks'], isLoading: false })
                    break;
                }
            }
        })

    }

    handleBackLinkClick = (id) => {
        console.log('handleBackLinkClick');
        console.log(id);
        this.setContent(id)
        // console.log(event);
    }

    render() {


        console.log('Post render');
        // console.log(this.state.backLinks);

        if (this.state.isLoading) {
            return <div>Loaidng...</div>
        } else {
            let card_text = this.state.content

            var showdown = require('showdown'),
                converter = new showdown.Converter(),
                text = card_text,
                html = converter.makeHtml(text);
            // console.log(html);

            // 反向链接
            let backLinksBox = <div></div>
            if (this.state.backLinks.length > 0) {
                let backLinks = this.state.backLinks.map((backLink) =>
                    <li key={backLink.id} >
                        <Link to={{ pathname: '/post/' + backLink.id}} >
                            <span onClick={this.handleBackLinkClick.bind(Event,backLink.id)}>
                                {backLink.title}
                            </span>
                        </Link>
                    </li>
                )

                backLinksBox = <div className='backLinks'>
                    <header>Link to this page</header>
                    <ul>
                        {backLinks}
                    </ul>
                </div>
            }


            // 滚动到顶部
            window.scrollTo(0, 0);

            return (
                <div>
                    <Nav />
                    <div className='container'>

                        <article dangerouslySetInnerHTML={{ __html: html }}></article>
                        {backLinksBox}

                    </div>
                </div>
            );
        }
    }

}

export default Post;
